import { type } from "@testing-library/user-event/dist/type";
import { INCREMENT, DECREMENT,RESET } from "./actionTypes";
export const increment = ()=>({
type: INCREMENT,
payload:2
});

export const decrement =()=>({
    type: DECREMENT
});
export const rest=()=>({
    type:RESET
})
