import Card from "./Card";
const NewsApp=()=>{
    const[search, setsearch] = useState("in");


function handleinput(e){
    setsearch(e.target.value)


}
    const api_url = `https://newsdata.io/api/1/latest?apikey=pub_68318a939d2c3ec2b8960c50c1d9f1794c903&country=${search}`    
    const API_key = "pub_68318a939d2c3ec2b8960c50c1d9f1794c903"

    async function fetch_api() {
        const res = await fetch(api_url)
        const data = await res.json();
        console.log(data)

    }
    // fetch_api()
    function zzzz(){
        alert("hi")
    }
    return(
        <>
    <nav class="navbar navbar-dark bg-primary">
        <div class="container-fluid">
        <div class="navbar-brand">Trendy News</div>
        <div class="navbar-brand">All News Top Headlines</div>
        <form class="d-flex">
        <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search" onChange={handleinput}/>
        <button class="btn btn-dark"onClick={zzzz}>Search </button> 
        </form>
            </div>    
</nav>
<h3 className="subheading">Stay Update with TrendyNews!</h3>
<div class="container text-center">
<button type="button" class="btn btn-primary">Sports</button>
<button type="button" class="btn btn-secondary">P</button>
<button type="button" class="btn btn-success">Success</button>
<button type="button" class="btn btn-danger">Danger</button>
<button type="button" class="btn btn-warning">Warning</button>
</div>
<Card/>
</>
)

}
export default NewsApp;
