  <div class="row row-cols-4 g-0.05">

    {data.map((i,j)=>{
        // console.log(i)
      return(

<div class="col">  
  <div className="card" style={{width: "20rem"}}>
  <img src= {i.urlToImage} className="card-img-top" alt="..."/>
