import { INCREMENT, DECREMENT } from "./actionTypes";
const initialState={
    count: 0,
};
const counterreducer= (state=initialState,action) =>{
switch(action.type){
    case INCREMENT:
    return{...state,count:state.count+1};
    case DECREMENT:
        return{...state, count:state.count-1};
        default:
            return state;
}
}
export default counterreducer;
