import React, { useState } from "react";
import Card from "./Card";
const NewsApp=()=>{
    const[search, setsearch] = useState("in");


function handleinput(e){
    console.log(e.target.value)
    setsearch(e.target.value)

}
    const api_url = `https://newsdata.io/api/1/latest?apikey=pub_68318a939d2c3ec2b8960c50c1d9f1794c903&q=${search}`    
    const API_key = "pub_68318a939d2c3ec2b8960c50c1d9f1794c903"

    async function fetch_api() {
        const res = await fetch(api_url)
        const data = await res.json();
        console.log(data.results)

    }

    return(
        <>
        <nav className="navbar navbar-dark bg-primary">
        <div className="container-fluid">
        <div className="navbar-brand">Trendy News</div>
        <div className="navbar-brand">All News Top Headlines</div>
        {/* <form className="d-flex" onSubmit={fetch_api}> */}
        <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" onChange={handleinput}/>
        <button className="btn btn-dark" onClick={fetch_api}>Search </button> 
        {/* </form> */}
            </div>    
</nav>
<h3 className="subheading">Stay Update with TrendyNews!</h3>
<div className="container text-center">
<button type="button" className="btn btn-primary">Sports</button>
<button type="button" className="btn btn-secondary">P</button>
<button type="button" className="btn btn-success">Success</button>
<button type="button" className="btn btn-danger">Danger</button>
<button type="button" className="btn btn-warning">Warning</button>
</div>
<Card/>
</>
)

}
export default NewsApp;
